mapper.update:
update oa_account set
status = 1,
lastLoginTime = #{loginTime}
where name= #{name}

update oa_account set
status = 0,
lastOutTime = #{logoutTime}
where name= #{name}

mapper.insert:
insert into oa_log(
	name,
	active,
	time
)values(
	#{name},
	#{active},
	#{loginTime}
)

mapper.select:
select a.* from ( select identityCard userBoth,userPhone ,userName from oa_wechat where 1=1
and userPhone LIKE "%"#{user.name}"%"
UNION select identityCard userBoth,userPhone ,userName from oa_wechat where 1=1
and userName LIKE "%"#{user.name}"%"
) a where 1=1 limit #{user.id},#{user.userSex}
